import React, { useState, useEffect } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ImgCarousel from './ImgCarousel';
import DisplayInfo from './DisplayInfo';


export default function ListCabins (props) {
    const [cabins, setCabins] = useState([]);
    useEffect(() => {
        fetch('/cabins')
            .then(response => response.json())
            .then(json => {
                setCabins(json);
            })
            .catch(error => console.log(error)); 
    }, []);
    return (
        <div>
            {
                cabins.map(cabin => {
                    const {
                        name, description, id, 
                        totalPrice, askingPrice, expenses, 
                        primaryRoomArea, livableArea, grossArea,
                        aditionalInfo,
                        img1, img2, img3,
                        createdOn
                    } = cabin;
                    cabin.images= [img1, img2, img3];

                    return (
                        <div key={id}>
                            <Row>
                                <ImgCarousel images={images} img1={img1} img2={img2} img3={img3} />
                                <DisplayInfo 
                                    name={name} description={description} id={id}
                                    totalPrice={totalPrice} askingPrice={askingPrice} expenses={expenses}
                                    primaryRoomArea={primaryRoomArea} livableArea={livableArea} grossArea={grossArea}
                                    aditionalInfo={aditionalInfo}
                                    img1={img1} img1={img2} img3={img3}
                                    createdOn={createdOn}
                                />
                            </Row>


                        </div>
                    )
                })
            }
        </div>
    )
}
