import React, { useState, useEffect } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import $ from 'jquery';
import ImgCarousel from './ImgCarousel';
import DisplayInfo from './DisplayInfo';
import CabNav from './CabNav';
import arrowDown from '../../../images/icons/arrow-down.svg';
import arrowUp from '../../../images/icons/arrow-up.svg';


export default function ListCabins (props) {
    let counter = 0;
    let cabinList = [];
    const [currentCabin, setCurrentCabin] = useState(0);
    const [cabins, setCabins] = useState([]);
    useEffect(() => {
        fetch('/cabins')
            .then(response => response.json())
            .then(json => {
                setCabins(json);
            })
            .catch(error => console.log(error));
    }, []);

    function nextCabin () {
        if (currentCabin === cabins.length - 1) {
            setCurrentCabin(currentCabin - currentCabin);
        } else {
        setCurrentCabin(currentCabin + 1)
        }

    }
    function prevCabin () {
        if (currentCabin === 0) {
            setCurrentCabin(cabins.length -1);
        } else {
        setCurrentCabin(currentCabin - 1)
        }
    }
    return (
        <div className='cabin'>
            {
                cabins.map(cabin => {
                    console.log(cabins.length);
                    cabinList.push(cabin);
                    console.log(cabinList);
                    cabin.images= [cabin.img1, cabin.img2, cabin.img3];
                    const {
                        name, description, id, 
                        totalPrice, askingPrice, expenses, 
                        primaryRoomArea, liveableArea, grossArea,
                        aditionalInfo,
                        img1, img2, img3, images,
                        createdOn
                    } = cabin;
                    if (currentCabin === 0) {
                        return (
                            <div key={id} id={'cabin' + counter} className='cabin__container active'>
                                <Row>
                                    <ImgCarousel images={images}>
                                    <div className='cabNav'>
                                        <span>
                                            <div onClick={() => {nextCabin()}} className='cabin__navButton' style={{backgroundImage: `url(${arrowDown})`}}></div>
                                        </span>
                                        <span>
                                            <div onClick={() => {prevCabin()}} className='cabin__navButton' style={{backgroundImage: `url(${arrowUp})`}}></div>
                                        </span>
                                    </div>
                                    </ImgCarousel>
                                    <DisplayInfo 
                                        name={name} description={description} id={id}
                                        totalPrice={totalPrice} askingPrice={askingPrice} expenses={expenses}
                                        primaryRoomArea={primaryRoomArea} liveableArea={liveableArea} grossArea={grossArea}
                                        aditionalInfo={aditionalInfo}
                                        img1={img1} img1={img2} img3={img3}
                                        createdOn={createdOn}
                                    />
                                </Row>
                            </div>
                        )
                    } else {
                        return (
                            <div key={id} id={'cabin' + counter} className='cabin__container'>
                                <Row>
                                    <ImgCarousel images={images}>
                                    <div className='cabNav'>
                                        <span>
                                            <div onClick={() => {nextCabin()}} className='cabin__navButton' style={{backgroundImage: `url(${arrowDown})`}}></div>
                                        </span>
                                        <span>
                                            <div onClick={() => {prevCabin()}} className='cabin__navButton' style={{backgroundImage: `url(${arrowUp})`}}></div>
                                        </span>
                                    </div>
                                    </ImgCarousel>
                                    <DisplayInfo 
                                        name={name} description={description} id={id}
                                        totalPrice={totalPrice} askingPrice={askingPrice} expenses={expenses}
                                        primaryRoomArea={primaryRoomArea} liveableArea={liveableArea} grossArea={grossArea}
                                        aditionalInfo={aditionalInfo}
                                        img1={img1} img1={img2} img3={img3}
                                        createdOn={createdOn}
                                    />
                                </Row>
                            </div>
                        )
                    }


                })
            }
            {console.log(currentCabin)}
        </div>
    )
}
