import React, {useState} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from "react-bootstrap/Form";
import Button from '../../Button';
import emailjs from 'emailjs-com';
import * as yup from "yup";
import { useForm } from "react-hook-form";
/*
const contactSchema = yup.object().shape({
	name: yup.string().min(2).required(),
    email: yup.string().email().required(),
    subject: yup.string().min(1).required(),
    message: yup.string().min(10).required()
});
*/
export default function ContactFrom (props) {
    const {mail, setMail} = useState({
        name: '',
        email: '',
        subject: '',
        message: ''
    })
    /*
    const { register, handleSubmit, errors } = useForm({
        validationSchema: contactSchema
    });
    */
    console.log(mail);
    function handleSubmit(e) {
        e.preventDefault();
        /*
        emailjs.sendForm('gmail', 'hoftun_utvikling_contact', e.target)
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
          */

    }

    return (
        <Col className='contact-form' sm='12' md='12' lg='8'>
            <Form onSubmit={handleSubmit(onSubmit)}>
                <h3 className='contact-form__header'>Send en Melding</h3>
                <Row>
                    <Col>
                        <Form.Group className='contact-form__item'>
                            <Form.Label>Navn</Form.Label>
                            <Form.Control name='name' type='text' placeholder='' />
                        </Form.Group>
                    </Col>
                    <Col>
                        <Form.Group className='contact-form__item'>
                            <Form.Label>E-Post</Form.Label>
                            <Form.Control name='email' type='email' placeholder='' />
                        </Form.Group>
                    </Col>
                </Row>
                <Form.Group className='contact-form__item'>
                    <Form.Label>Emne</Form.Label>
                    <Form.Control size='lg' name='subject' type='text' placeholder='' />
                </Form.Group>
                <Form.Group className='contact-form__item'>
                    <Form.Label>Melding</Form.Label>
                    <Form.Control size='sm' rows='3' as='textarea' name='message' type='text' placeholder='' />
                </Form.Group>
                <Form.Group className='contact-form__item contact-form__item--submit'>
                    <Button type='submit' value='Send'>
                        <span>Send Melding</span>
                    </Button>
                </Form.Group>
            </Form>
        </Col>
    )
}
