import React, { useState, useEffect } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import $ from 'jquery';
import ImgCarousel from './ImgCarousel';
import DisplayInfo from './DisplayInfo';
import CabNav from './CabNav';
import arrowDown from '../../../images/icons/arrow-down.svg';
import arrowUp from '../../../images/icons/arrow-up.svg';


export default function ListCabins (props) {
    let counter = 0;
    const [currentCabin, setCurrentCabin] = useState(0);
    const [cabins, setCabins] = useState([]);
    useEffect(() => {
        fetch('/cabins')
            .then(response => response.json())
            .then(json => {
                setCabins(json);
            })
            .catch(error => console.log(error));
    }, []);

    function nextCabin () {
        setCurrentCabin(currentCabin + 1)

    }
    function prevCabin () {
        setCurrentCabin(currentCabin - 1)

    }
    let cabinList = [];
    return (
        <div classname='cabin'>
            {
                cabins.map(cabin => {
                    console.log(counter)
                    cabinList.push(cabin);
                    console.log(cabinList);
                    cabin.images= [cabin.img1, cabin.img2, cabin.img3];
                    const {
                        name, description, id, 
                        totalPrice, askingPrice, expenses, 
                        primaryRoomArea, liveableArea, grossArea,
                        aditionalInfo,
                        img1, img2, img3, images,
                        createdOn
                    } = cabin;
                    return (
                        <div key={id} id={'cabin' + counter} className='cabin__container'>
                            <Row>
                                <ImgCarousel images={images}>
                                <div className='cabNav'>
                                    <span>
                                        <div onClick={() => {nextCabin()}} className='cabNav__button' style={{backgroundImage: `url(${arrowDown})`}}></div>
                                    </span>
                                    <span>
                                        <div onClick={() => {prevCabin()}} className='cabNav__button' style={{backgroundImage: `url(${arrowUp})`}}></div>
                                    </span>
                                </div>
                                </ImgCarousel>
                                <DisplayInfo 
                                    name={name} description={description} id={id}
                                    totalPrice={totalPrice} askingPrice={askingPrice} expenses={expenses}
                                    primaryRoomArea={primaryRoomArea} liveableArea={liveableArea} grossArea={grossArea}
                                    aditionalInfo={aditionalInfo}
                                    img1={img1} img1={img2} img3={img3}
                                    createdOn={createdOn}
                                />
                            </Row>
                        </div>
                    )
                })
            }
        </div>
    )
}
