import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav from '../Nav'
import {contactPage} from '../../constants/constants';

export default function About (props) {
    const {firstSection, secondSection, infoImage} = contactPage.aboutInfo;
    return (
        <div>
            <Nav />
            <Container className='about-info'>
                <h3>Om oss</h3>
                <Row>    
                    <p>{firstSection}</p>
                    <p>{secondSection}</p>
                    <img src={infoImage} className='about-info__image' alt='Oversiktsbilde av hytte' />
                </Row>
            </Container>
        </div>
    )
}
