    return (
        <Col lg='8' md='8'>
            <img src={props.images[cabinImg]} width='100%' />
            <div className='imgNav'>
                <span>
                    <div onClick={() => {setCabinImg(cabinImg + 1)}} className='imgNav__button' style={{backgroundImage: `url(${arrowRight})`}}></div>
                </span>
                <span>
                    <div onClick={() => {setCabinImg(cabinImg + 1)}} className='imgNav__button' style={{backgroundImage: `url(${arrowLeft})`}}></div>
                </span>
            </div>
        </Col>
    )
}
