import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Button from '../../Button'
import * as yup from "yup";
import { useForm } from "react-hook-form";

/*function newCabin() {
    const options = {
      method: 'POST',
      headers : {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(contactPage)

    }
    fetch('/cabins', options)
      .then(response => response.json())
      .then(data => {
        console.log(data);
      })
};*/


const cabinSchema = yup.object().shape({
    name: yup.string().required('Hytten må ha ett navn, eventuelt overskrift istedet'),
    description: yup.string().required('Hytten trenger en beskrivlse, eventuelt en innledning eller infotekst'),
    askingPrice: yup.number().required('Hytten trenger en pris'),
    expenses: yup.number(),
    totalPrice: yup.number(),
    liveableArea: yup.number().required('Hytten må ha bruksareal'),
    primaryRoomArea: yup.number(),
    grossArea: yup.number(),
    aditionalInfo: yup.string(),
    timestamp: yup.date().default(function(){
        return new Date();
    })
});

export default function PostCabin (props) {
    const { register, handleSubmit, errors } = useForm({
        validationSchema: cabinSchema
    });

    function onSubmit(data) {
        console.log("data", data);
    }
    return(
        <div className='add-cabin'>
            <Form onSubmit={handleSubmit(onSubmit)}>
               <Container>
                    <Row>
                        <h3 className='contact-form__header'>Legg til ny hytte</h3>
                        <Form.Group className='contact-form__item'>
                            <Form.Control name='cabin-name' type='text' placeholder='Hyttenavn' ref={register({ required: true })}/>
                            {errors.name && <p className="add-cabin__error">Name is required</p>}
                        </Form.Group>
                        <Form.Group className='contact-form__item'>
                            <Form.Control as='textarea' rows='3' name='cabin-description' type='text' placeholder='Hytte beskrivelse' ref={register({ required: true })}/>
                            {errors.description && <p className="add-cabin__error">Name is required</p>}
                        </Form.Group>
                        <Col lg='6' md='6' sm='12'>
                            <p>Priser: </p>
                            <Form.Group className='contact-form__item'>
                                <Form.Control name='asking-price' type='text' placeholder='Prisantydning' ref={register({ required: true })}/>
                                {errors.askingPrice && <p className="add-cabin__error">Name is required</p>}
                            </Form.Group>
                            <Form.Group className='contact-form__item'>
                                <Form.Control name='expenses' type='text' placeholder='Omkostninger' />
                            </Form.Group>
                            <Form.Group className='contact-form__item'>
                                <Form.Control name='total-price' type='text' placeholder='Total pris' />
                            </Form.Group>
                        </Col>
                        <Col lg='6' md='6' sm='12'>
                            <p>Mål: </p>
                            <Form.Group className='contact-form__item'>
                                <Form.Control name='livable-area' type='text' placeholder='Bruksareal' ref={register({ required: true })}/>
                                {errors.liveableArea && <p className="add-cabin__error">Name is required</p>}
                            </Form.Group>
                            <Form.Group className='contact-form__item'>
                                <Form.Control name='primary-room-area' type='text' placeholder='Primærrom areal' />
                            </Form.Group>
                            <Form.Group className='contact-form__item'>
                                <Form.Control name='gross-area' type='text' placeholder='Bruttoareal' />
                            </Form.Group>
                        </Col>
                        <Form.Group className='contact-form__item'>
                            <Form.Control as='textarea' rows='3' name='aditional-info' type='text' placeholder='Tilleggsinfo' />
                        </Form.Group>
                        <Form.Group className='contact-form__item contact-form__item--submit'>
                            <Button type='submit'>
                                <span>Legg til hytte</span>
                            </Button>
                        </Form.Group>
                    </Row>
                </Container>
            </Form>
        </div>
    )
}
