import React from 'react';
import Col from 'react-bootstrap/Col';
import Form from "react-bootstrap/Form";
import * as yup from "yup";
import { useForm } from "react-hook-form";

const schema = yup.object().shape({
	fullName: yup.string().min(2).required("Name is"),
    email: yup.string().email().required("Valid Email is required"),
    message: yup.string().min(10).required("Message must be 10 or more characters long"),
});

export default function ContactFrom (props) {
    return (
        <Col className='contact-form' sm='12' md='12' lg='6'>
            <Form>
                <h3 className='contact-form__header'>Ta Kontakt</h3>
                <div className='contact-form__item'>
                    <input name='name' type='text' placeholder='Navn...' />
                </div>
                <div className='contact-form__item'>
                    <input name='email' type='text' placeholder='Epost...' />
                </div>
                <div className='contact-form__item'>
                    <textarea name='message' type='textarea' placeholder='Message...' />
                </div>
            </Form>
        </Col>
    )
}
