import React, {useState} from 'react';
import Col from 'react-bootstrap/Col';
import ImgNav from './ImgNav';
import arrowRight from '../../../images/icons/arrow-right.svg';
import arrowLeft from '../../../images/icons/arrow-left.svg';


export default function ImgCarousel (props) {
    const [cabinImg, setCabinImg] = useState(0);
    const [imageIndex, setImageIndex] = useState(0);

    /*function nextImage (e) {
        setCabinImg++;
    }
    function prevImage (e) {
        setCabinImg--;
    }*/
    //sett inn imgNav i  return 
    return (
        <Col lg='8' md='8'>
            <img src={props.images[cabinImg]} width='100%' />
            <div className='imgNav'>
                <span>
                    <div onClick={() => {setCabinImg(cabinImg + 1)}} className='imgNav__button' style={{backgroundImage: `url(${arrowRight})`}}></div>
                </span>
                <span>
                    <div onClick={() => {setCabinImg(cabinImg - 1)}} className='imgNav__button' style={{backgroundImage: `url(${arrowLeft})`}}></div>
                </span>
            </div>
        </Col>
    )
}
