import React, { useRef } from 'react';
import $ from 'jquery';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

export default function ContactInfo (props) {
    const copyRef= useRef();
    function copyToClipboard() {
        var text = $(".contact-info__mail-adress").get(0); // Grab the node of the element
        console.log(text)
        var selection = window.getSelection(); // Get the Selection object
        var range = document.createRange(); // Create a new range
        range.selectNodeContents(text); // Select the content of the node from line 1
        selection.removeAllRanges(); // Delete any old ranges
        selection.addRange(range); // Add the range to selection
        document.execCommand('copy'); // Execute the command
    }
    return (
        <Col className='contact-info' sm='12' md='6' lg='4'>
            <img src={props.image} className='contact-info__image' alt='Bilde av Magne Hoftun' />
            <h3 className='contact-info__name'>{props.name}</h3>
            <Row>
                <Col lg='10' md='10'>
                    <p className='contact-info__mail'><span className='contact-info__mail--bold'>Email:</span><span className='contact-info__mail-adress'>{props.mail}</span></p>
                </Col>
                <Col lg='2' md='2'>
                    <div onclick={() => copyToClipboard()} ref={copyRef} className='contact-info__copy'></div>
                </Col>
            </Row>
            <p className='contact-info__phone'><span className='contact-info__phone--bold'>Telefon:</span> {props.phone}</p>
        </Col>
    )
}
