import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {contactPage} from '../../constants/constants';

export default function About (props) {
    const {firstSection, secondSection, infoImage} = contactPage.aboutInfo;
    return (
        <Container className='about-info' sm='12' md='12' lg='12'>
            <h3>Om oss</h3>
            <Row>    
                <Col>
                    <p>{firstSection}</p>
                </Col>
                <Col>
                    <p>{secondSection}</p>
                </Col>
                <Col>
                    <p>{infoImage}</p>
                </Col>
                <img src={props.image} className='about-info__image' alt='Oversiktsbilde av hytte' />
            </Row>
        </Container>
    )
}
